(1, 'Default Form', '<p class=\"sib-email-area\">\r\n    <label class=\"sib-email-area\">Email Address*</label>\r\n    <input type=\"email\" class=\"sib-email-area\" name=\"email\" required=\"required\">\r\n</p>\r\n<p class=\"sib-NAME-area\">\r\n    <label class=\"sib-NAME-area\">Name</label>\r\n    <input type=\"text\" class=\"sib-NAME-area\" name=\"NAME\" >\r\n</p>\r\n<p>\r\n    <input type=\"submit\" class=\"sib-default-btn\" value=\"Subscribe\">\r\n</p>', '[form] {\r\n    padding: 5px;\r\n    -moz-box-sizing:border-box;\r\n    -webkit-box-sizing: border-box;\r\n    box-sizing: border-box;\r\n}\r\n[form] input[type=text],[form] input[type=email], [form] select {\r\n    width: 100%;\r\n    border: 1px solid #bbb;\r\n    height: auto;\r\n    margin: 5px 0 0 0;\r\n}\r\n[form] .sib-default-btn {\r\n    margin: 5px 0;\r\n    padding: 6px 12px;\r\n    color:#fff;\r\n    background-color: #333;\r\n    border-color: #2E2E2E;\r\n    font-size: 14px;\r\n    font-weight:400;\r\n    line-height: 1.4285;\r\n    text-align: center;\r\n    cursor: pointer;\r\n    vertical-align: middle;\r\n    -webkit-user-select:none;\r\n    -moz-user-select:none;\r\n    -ms-user-select:none;\r\n    user-select:none;\r\n    white-space: normal;\r\n    border:1px solid transparent;\r\n    border-radius: 3px;\r\n}\r\n[form] .sib-default-btn:hover {\r\n    background-color: #444;\r\n}\r\n[form] p{\r\n    margin: 10px 0 0 0;\r\n}', 1, 'a:1:{i:0;s:1:\"2\";}', -1, 0, 0, NULL, NULL, 'Thank you, you have successfully registered !', 'Something wrong occured', 'You have already registered', 'Your email address is invalid', 'email,NAME', '2017-06-21', 1, 0, NULL, NULL, 1, NULL);
